import logo from './logo.svg';
import './App.css';
import sisebuto from './reyes/rey_sisebuto.png';
import leogivildo from './reyes/rey_leogivildo.png';
import atanagildo from './reyes/rey_atanagildo.png';
import { useRef } from 'react';



function App() {
  const refCaja=useRef();
  const cambio=23.16
  function incrementar(e) {

    e.target.innerHTML=Number(e.target.innerHTML)+1;
    if (e.target.innerHTM>=10) {
      e.target.innerHTML=1;

    }
    if (e.target.innerHTM>=8) {
      e.target.style.backgroundColor="red";
    }else{ e.target.style.backgroundColor="white";}

    }
    function cambiar(e) {
      if ( e.target.src.includes("sisebuto")) {
        e.target.src=leogivildo;
      } else {
        e.target.src=sisebuto;
      }


    console.log(e.target);
    }
    function convertir(e) {
      refCaja.current.innerHTML=Number(refCaja.current.innerHTML)*cambio;
    }

    function lectura(e) {
      refCaja.current.innerHTML=e.target.value ;
    }

  return (
    <>
      <div ref={refCaja} className='caja' onClick={incrementar}>1</div>
      <button onClick={convertir}>aceptar</button>
      <div><img src={sisebuto} onClick={cambiar}></img></div>
      <input onChange={lectura} className='campo'></input>


    </>


  );
}

export default App;
