import logo from './logo.svg';
import './App.css';
import sisebuto from './reyes/rey_sisebuto.png';
import leogivildo from './reyes/rey_leogivildo.png';
import atanagildo from './reyes/rey_atanagildo.png';
import incognito from './reyes/rey_incognito.png';
import './reyes.css';



function App() {
  let nombres = ["Sisebuto", "Leogivildo", "atanagildo"];
  const cambiartexto = (e) => {

    if (e.target.innerHTML == "visto") {
      e.target.innerHTML="";
    }else{
      e.target.innerHTML="visto";
    }
  }

  const cambiarImagen =(e)=>{
    if (e.target.src.includes("incognito")) {
      e.target.style.visibility="hidden";
    } else {
      e.target.src=incognito
    }
      e.target.parentNode.style.backgroundColor="white";
  }
  return (
    <div className="contenedor">
      <div className='caja'>
        <img onClick={cambiarImagen}src={sisebuto}></img>
        <div onClick={cambiartexto} className='nombre'>{nombres[0]}</div>
      </div>
      <div  className='caja'>
        <img onClick={cambiarImagen} src={leogivildo}></img>
        <div onClick={cambiartexto} className='nombre'>{nombres[1]}</div>
      </div>
      <div className='caja'>
        <img onClick={cambiarImagen} src={atanagildo}></img>
        <div onClick={cambiartexto} className='nombre'>{nombres[2]}</div>
      </div>
    </div>
  );
}

export default App;
